class UsersDB{constructor(){this.users=[]}addUser(s){this.users=[...this.users,s]}getAllUsers(){return this.users}getUser(s){return this.users.find(e=>e.id===s)}getUsersByRoom(s){return this.users.filter(e=>e.room===s)}removeUser(s){this.users=this.users.filter(e=>e.id!==s)}setTypingStatus(s,e){const r=this.users.findIndex(e=>e.id===s);this.users[r].typingStatus=e}}module.exports=()=>new UsersDB;